const { app, Menu } = require('electron').remote

const setLabel = e => {
  window.localStorage.theme = e.label
  window.events.emit('editor:theme', e.label)
}

const template = [
  {
    label: 'Edit',
    submenu: [
      {role: 'undo'},
      {role: 'redo'},
      {type: 'separator'},
      {role: 'cut'},
      {role: 'copy'},
      {role: 'paste'},
      {role: 'pasteandmatchstyle'},
      {role: 'delete'},
      {role: 'selectall'}
    ]
  },
  {
    label: 'View',
    submenu: [
      {role: 'toggledevtools'},
      {type: 'separator'},
      {role: 'resetzoom'},
      {role: 'zoomin'},
      {role: 'zoomout'},
      {type: 'separator'},
      {role: 'togglefullscreen'}
    ]
  },
  {
    role: 'window',
    submenu: [
      {role: 'minimize'}
    ]
  },
  {
    label: 'Options',
    submenu: [
      {
        label: 'Document Window',
        type: 'checkbox',
        checked: JSON.parse(window.localStorage.sandbox || false),
        click: () => {
          window.events.emit('sandbox')
        }
      },
      {
        label: 'Line Numbers',
        type: 'checkbox',
        checked: JSON.parse(window.localStorage.lineNumbers || false),
        click: () => {
          window.events.emit('editor:linenumbers')
        }
      }
    ]
  },
  {
    label: 'Theme',
    submenu: [
      { label: 'Light', click: setLabel },
      { label: 'Dark', click: setLabel }
    ]
  }
]

if (process.platform === 'darwin') {
  template.unshift({
    label: app.getName(),
    submenu: [
      {role: 'about'},
      {type: 'separator'},
      {role: 'services', submenu: []},
      {type: 'separator'},
      {role: 'hide'},
      {role: 'hideothers'},
      {role: 'unhide'},
      {type: 'separator'},
      {role: 'quit'}
    ]
  })
}

const menu = Menu.buildFromTemplate(template)
Menu.setApplicationMenu(menu)
